import Vue from "vue";
import VueRouter from "vue-router";
import HomePage from "./views/HomePage";
import ErrorPage from "./views/ErrorPage";

Vue.use(VueRouter);

const router = new VueRouter({
  mode: "history",
  routes: [
    {
      path: "/",
      component: HomePage,
    },
    {
      path: "*",
      component: ErrorPage,
    },
  ],
});

export default router;
